   assert (defaultExtension("file", ".ext")     == "file.ext");
   assert (defaultExtension("file.", "ext")     == "file.");
   assert (defaultExtension("file.old", "new")  == "file.old");
   assert (defaultExtension("file.old", ".new") == "file.old");
